svg.script:
  var stop=true;
  var intervalId;
  var frameId = -1;
  var totalDuration = 1000;
  var totalFrame = totalDuration/16;
  var object;
  
  function init(evt) {
   object = evt.target.ownerDocument.getElementById('rot');
   
   togglePause(evt);
  }
  
  function animate() {
     if(++frameId>totalFrame) frameId=0;
	 object.setAttribute('r',100.0*frameId/totalFrame);
     //object.setAttribute('transform', 'rotate(' + degrees + ')');
    }
  
  function togglePause(evt) {
   if(stop) {
    stop=false;
	intervalId=setInterval(animate, 16);
   } else {
    stop=true;
	clearInterval(intervalId);
   }
  }
